function holidayContent(){

    var matched = -1;
    var originalOutput="<small>*注: 节日大多在所列日期的凌晨3-4点生效或截止，详见：<a href='http://guildwars.huijiwiki.com/wiki/%E6%97%A5%E5%8E%86#.E8.8A.82.E6.97.A5.E5.88.97.E8.A1.A8'>日历列表</a></small><br>";

    var tThisMoment = new Date();
    var tCurrentYear = tThisMoment.getUTCFullYear();

    var tDateR = new Date(tCurrentYear+festivalData[0][1]);
    var tDateL = new Date(tCurrentYear+festivalData[0][0]);

    if ((tThisMoment >= tDateL) || (tThisMoment <= tDateR)){
      //((a < b) ? 2 : 3)
      var sMonth = parseInt(festivalData[0][0].slice(1,3));
      var sDate = parseInt(festivalData[0][0].slice(4,6));
      var eMonth = parseInt(festivalData[0][1].slice(1,3));
      var eDate = parseInt(festivalData[0][1].slice(4,6));

      var endDateDisplay = eDate+1;
      if ((festivalData[0][2] === "万圣节") || (festivalData[0][2] === "冬庆节")) {
        endDateDisplay = endDateDisplay - 1;
      }
      if ((sMonth == 1) && ((sDate+1)==32)){
          sMonth = 2;
          sDate = 0
      }

      originalOutput = originalOutput + '<a href='+festivalData[0][5]+'>' + festivalData[0][2] + '</a>' + " (" + sMonth + "月" + (sDate+1) + "日" + " 至 " +
      eMonth + "月" + endDateDisplay + "日)" + ((festivalData[0][3] ==='') ? '' : '<br>' + (festivalData[0][3])) + '<br>';
      matched = 0;
    }

    for (var i = 1; i<13; i++){
        var startDate = new Date(tCurrentYear+festivalData[i][0]);
        var endDate = new Date(tCurrentYear+festivalData[i][1]);

        var sMonth = parseInt(festivalData[i][0].slice(1,3));
        var sDate = parseInt(festivalData[i][0].slice(4,6));
        var eMonth = parseInt(festivalData[i][1].slice(1,3));
        var eDate = parseInt(festivalData[i][1].slice(4,6));

        var endDateDisplay = eDate+1;
        if ((festivalData[i][2] === "万圣节") || (festivalData[i][2] === "冬庆节")) {
          endDateDisplay = endDateDisplay - 1;
        }
        if ((sMonth == 1) && ((sDate+1)==32)){
            sMonth = 2;
            sDate = 0
        }
        //possibly work in the 1 min of 19:01 boundary
        if ((tThisMoment >= startDate) && (tThisMoment <= endDate)){
            originalOutput = originalOutput +  '<a href='+festivalData[i][5]+'>' + festivalData[i][2] + '</a>' + " (" + sMonth + "月" + (sDate+1) + "日" + " 至 " +
            eMonth + "月" + endDateDisplay + "日)" + ((festivalData[i][3] ==='') ? '' : '<br>' + (festivalData[i][3])) + '<br>';
            matched = i;
        }
    }

    var nextFestivalIndex = 0;

    if (matched > -1 ) {
      (matched === 12)?(nextFestivalIndex=0):(nextFestivalIndex=matched+1);

      var NstartDate = new Date(tCurrentYear+festivalData[nextFestivalIndex][0]);
      var NendDate = new Date(tCurrentYear+festivalData[nextFestivalIndex][1]);
	  
	  //didn't bother to change the year in tThisMoment, the following would be wrong for all indexes, not just 0 and 1, but as it happens, only 0 and 1 would lead to an error
      if ((tThisMoment > NendDate) && (nextFestivalIndex!=0) && (nextFestivalIndex!=1)){
        nextFestivalIndex += 1;
      }

      var sMonth = parseInt(festivalData[nextFestivalIndex][0].slice(1,3));
      var sDate = parseInt(festivalData[nextFestivalIndex][0].slice(4,6));
      var eMonth = parseInt(festivalData[nextFestivalIndex][1].slice(1,3));
      var eDate = parseInt(festivalData[nextFestivalIndex][1].slice(4,6));

          var endDateDisplay = eDate+1;
          if ((festivalData[nextFestivalIndex][2] === "万圣节") || (festivalData[nextFestivalIndex][2] === "冬庆节")) {
            endDateDisplay = endDateDisplay - 1;
          }
          if ((sMonth == 1) && ((sDate+1)==32)){
              sMonth = 2;
              sDate = 0
          }
          originalOutput = originalOutput + "<hr>[节日预告]<br>"  +  '<a href='+festivalData[nextFestivalIndex][5]+'>' + festivalData[nextFestivalIndex][2] + '</a>' + " (" + sMonth + "月" + (sDate+1) + "日" + " 至 " +
          eMonth + "月" + endDateDisplay + "日)" +
          ((festivalData[nextFestivalIndex][3] ==='') ? '' : '<br>' + (festivalData[nextFestivalIndex][3])) + '<br>';

          document.getElementById('jieri').innerHTML = originalOutput;
          return 0;
    } else {
      originalOutput = originalOutput + "[当前无节日]<br>";

      var timeDiffArray = []
      for (var i=0;i<13;i++){
        var tStartDate = new Date(tCurrentYear+festivalData[i][0]);
        if ((tStartDate - tThisMoment) < 0) {
          timeDiffArray.push(50000000);
        } else {
            timeDiffArray.push((tStartDate - tThisMoment)/1000);
        }
      }

      var resultIndex = 0;
      var minValue = timeDiffArray[0];
      for (var i = 1; i < timeDiffArray.length; i++) {
        if (timeDiffArray[i] < minValue) {
          minValue = timeDiffArray[i];
          resultIndex = i;
        }
      }

      var sMonth = parseInt(festivalData[resultIndex][0].slice(1,3));
      var sDate = parseInt(festivalData[resultIndex][0].slice(4,6));
      var eMonth = parseInt(festivalData[resultIndex][1].slice(1,3));
      var eDate = parseInt(festivalData[resultIndex][1].slice(4,6));

      var endDateDisplay = eDate+1;
      if ((festivalData[resultIndex][2] === "万圣节") || (festivalData[resultIndex][2] === "冬庆节")) {
          endDateDisplay = endDateDisplay - 1;
      }
      if ((sMonth == 1) && ((sDate+1)==32)){
          sMonth = 2;
          sDate = 0
      }
          originalOutput = originalOutput + "<hr>[节日预告]<br>"  + '<a href='+festivalData[resultIndex][5]+'>' + festivalData[resultIndex][2] + '</a>' + " (" + sMonth + "月" + (sDate+1) + "日" + " 至 " +
          eMonth + "月" + endDateDisplay + "日)" +
          ((festivalData[resultIndex][3] ==='') ? '' : ('<br>' + festivalData[resultIndex][3])) + '<br>';

      document.getElementById('jieri').innerHTML = originalOutput;
      return 0;
    }

}

function difference(arr1,arr2) {

  var result=[]
  for(var i=0;i<arr1.length;i++){
     var keepflag = true;
     for (var j = 0;j<arr2.length;j++){
         if (arr1[i]===arr2[j]){
            keepflag = false;
            break;
         }
     }
     if (keepflag){
         result.push(arr1[i])
     }
  }

  return result;
 }

